import {Component, Input, OnInit} from '@angular/core';
import {Observable} from "rxjs";
import {Book} from "../../../models/book";
import { HttpService } from '../../../services/http.service';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { switchMap } from 'rxjs/operators';
import { Location } from '@angular/common';
import {AddService} from "../../../services/add.service";
import {Users} from "../../../models/users";
import {UsersComponent} from "../../users/users.component";

@Component({
  selector: 'app-book-details',
  templateUrl: './book-details.component.html',
  styleUrls: ['./book-details.component.css']
})
export class BookDetailsComponent implements OnInit {
  bookDetails!: Observable<Book>;
  showMe:boolean=false
  constructor(
    private http: HttpService,
    private route: ActivatedRoute,
    private router: Router,
    private location: Location,
    private add: AddService
  ) {}

  ngOnInit() {
    this.bookDetails = this.route.paramMap.pipe(
      switchMap((params: ParamMap) => this.http.getBook(params.get('id')))
    );

  }

  goToBooks() {
    this.location.back();
  }

  addToUser(book: Book){
    this.add.addToList(book);
    return this.location.back();
  }

  toogleTag(){
    this.showMe = !this.showMe;
  }
}
<div class="row" *ngIf="bookDetails | async as book">
  <div class="col s4"><img [src]="book.poster" /></div>
  <table class="col s8">
    <tbody>
    <tr>
      <td>Title</td>
      <td>{{ book.title }}</td>
    </tr>
    <tr>
      <td>Author</td>
      <td>{{ book.author }}</td>
    </tr>
    <tr>
      <td>ISBN</td>
      <td>{{ book.ISBN }}</td>
    </tr>
    </tbody>
  </table>
  <button class="btn black" (click)="goToBooks()">Back</button>
  <button class="btn black"  (click)="toogleTag()">Wypożycz</button>
  <app-users *ngIf="showMe"></app-users>
</div>
