import { Component, OnInit } from '@angular/core';
import {Observable} from "rxjs";
import {Book} from "../../models/book";
import {HttpService} from "../../services/http.service";

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit {
  searchValue!: string;
  Books!: Book[];
  books!: Observable<Book[]>;
  constructor(private http: HttpService) { }

  ngOnInit(): void {
    this.books = this.http.getBooks();
  }

  search(Books: Book[], searchValue: string): Book[]{
    if (!Books || !searchValue) {
      return Books;
    }
    return Books.filter(books =>
        books.title.toLocaleLowerCase().includes(searchValue.toLocaleLowerCase()) ||
        books.author.toLocaleLowerCase().includes(searchValue.toLocaleLowerCase())
    );
  };

}
<div class="grid">
  <div>
    <input type="text" id="search" [(ngModel)]="searchValue" *ngIf="search(Books, searchValue)"/>
  </div>
  <app-book-layout
    *ngFor="let book of books | async"
    [book]="book"
    >
  </app-book-layout>
</div>
